import React, { useState, useEffect } from 'react'
import { http } from '../../axios'
import DeleteIcon from '@material-ui/icons/Delete'
import EditIcon from '@material-ui/icons/Edit'


function ViewAddress({ reload }) {

    const [viewAddress, setviewAddress] = useState([])
    const [reRender,setreRender] = useState()

    useEffect(() => {
        http.get("address")
            .then(res => {
                setviewAddress(res.data)
            })
            .catch(err => {
                console.log(err)
            })
    }, [reload,reRender])

    const editItem=(data)=>{
        http.put("address")
        .then(res=>{
            console.log(res.data)
            setreRender(res.data)

        })
        .catch(err=>{
            console.log(err)
        })
    }

    const deleteItem=(id)=>{
        http.delete("address",{params:{id:id}})
        .then(res=>{
            console.log(res.data)
            setreRender(res.data)
            if(res.data.ok === 1){
                alert("Address Deleted SucccessFully")
            }
            else{
                alert("Address Not Deleted")
            }
        })
        .catch(err=>{
            alert("Error Deletion" ,err)
        })
    }

    if(viewAddress.length > 0){
        return (
            <div>
                <table className="w3-table-all">
                    <thead>
                        <tr>
                            <td>Name</td>
                            <td>GSTIN</td>
                            <td>Email</td>
                            <td>Phone</td>
                            <td>Address</td>
                            <td>Edit</td>
                            <td>Delete</td>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            viewAddress.map(data => (
                                <tr key={data._id} >
                                    <td>{data.name}</td>
                                    <td>{data.gstin}</td>
                                    <td>{data.email}</td>
                                    <td>{data.contact}</td>
                                    <td>{data.address}, {data.city}, {data.state}, {data.country}, {data.pincode}</td> 
                                    <td className="w3-text-green" onClick={()=>editItem(data)}><EditIcon /></td>
                                    <td className="w3-text-red" onClick={()=>deleteItem(data._id)}><DeleteIcon /></td>                               
                                </tr>
                            ))
                        }
                    </tbody>
                </table>

            </div>
        )
    }    
    else{
        return null
    }

}

export default ViewAddress
