import {type, sidemenu} from './Type'



const inittialState = {
    sideMenu:"sales"
}

const Reducer = (state = inittialState, action )=>{
    switch(action.type){
        case sidemenu:
            return {
                ...state,
                sideMenu:action.data     
            }
        default : return state 
    }
}

export default Reducer
